<!-- <p>login works!</p> -->
<div class="lg:w-3/12 mt-10 p-10 shadow-lg mx-auto">
  <h3 class="text-3xl text-bold py-5">Reset your password</h3>
  <form [formGroup]="resetForm" class="flex flex-col">
    <span class="mb-2">Please use strong password</span>
    <div class="flex flex-col mt-4 mb-1">
      <input
        formControlName="password"
        class="p-2 border border-gray-300 rounded"
        type="password"
        placeholder="Password"
      />
      <span
        *ngIf="
          resetForm.hasError('required', 'password') &&
          resetForm.controls['password'].dirty
        "
        class="text-red-500"
        >*Password is required</span
      >
    </div>

    <div class="flex flex-col mt-4 mb-1">
      <input
        formControlName="confirmPassword"
        class="p-2 border border-gray-300 rounded"
        type="password"
        placeholder="Confirm Password"
      />
      <span
        *ngIf="
          resetForm.hasError('required', 'confirmPassword') &&
          resetForm.controls['confirmPassword'].dirty
        "
        class="text-red-500"
        >*Confirm Password is required</span
      >
      <span
        *ngIf="
          resetForm.hasError('confirmPasswordValidators', 'confirmPassword')
        "
        class="text-red-500"
        >*Password didn't match</span
      >
    </div>

    <button
      [disabled]="resetForm.invalid"
      [class.disabled]="resetForm.invalid"
      (click)="reset()"
      class="bg-indigo-500 w-1/4 mr-3 p-2 rounded-md mt-3 text-white"
    >
      submit
    </button>
  </form>
</div>
