import { Component, OnInit, inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import {
  FormBuilder,
  FormGroup,
  ReactiveFormsModule,
  Validators,
} from '@angular/forms';
import { RouterModule } from '@angular/router';
import { AuthService } from 'src/app/services/auth.service';

@Component({
  selector: 'app-forget-password',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule, RouterModule],
  templateUrl: './forget-password.component.html',
  styleUrls: ['./forget-password.component.scss'],
})
export default class ForgetPasswordComponent implements OnInit {
  forgetForm!: FormGroup;
  fb = inject(FormBuilder);
  authService = inject(AuthService);
  ngOnInit(): void {
    this.forgetForm = this.fb.group({
      email: ['', Validators.compose([Validators.required, Validators.email])],
    });
  }

  submit() {
    this.authService.sendEmailService(this.forgetForm.value.email).subscribe({
      next: (res) => {
        alert(res.message);
        this.forgetForm.reset();
      },
      error: (err) => {
        alert(err.error.message);
      },
    });
  }
}
<!-- <p>forget-password works!</p> -->

<!-- <p>login works!</p> -->
<div class="lg:w-3/12 mt-10 p-10 shadow-lg mx-auto">
  <h3 class="text-3xl text-center font-semibold py-5">Forget Password</h3>
  <form [formGroup]="forgetForm" class="flex flex-col">
    <span class="mb-4">
      Enter your email and we will send you instruction on how to reset your
      password.
    </span>
    <div class="flex flex-col mt-2 mb-1">
      <input
        formControlName="email"
        class="p-2 border border-gray-300 rounded"
        type="text"
        placeholder="Email"
      />
      <span
        *ngIf="
          forgetForm.hasError('required', 'email') &&
          forgetForm.controls['email'].dirty
        "
        class="text-red-500"
        >*Email is required</span
      >
      <span *ngIf="forgetForm.hasError('email', 'email')" class="text-red-500"
        >*Email is invalid</span
      >
    </div>
    <div class="mt-2">
      <button
        [disabled]="forgetForm.invalid"
        [class.disabled]="forgetForm.invalid"
        (click)="submit()"
        class="bg-indigo-500 w-1/4 mr-3 p-2 rounded-md text-gray-50"
      >
        Submit
      </button>
      <button
        routerLink="/login"
        class="bg-gray-500 w-1/4 p-2 rounded-md text-gray-50"
      >
        Cancel
      </button>
    </div>
  </form>
</div>
