import { Component, inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import {
  FormBuilder,
  FormGroup,
  ReactiveFormsModule,
  Validators,
} from '@angular/forms';
import { AuthService } from 'src/app/services/auth.service';
import { Router, RouterModule } from '@angular/router';

@Component({
  selector: 'app-login',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule, RouterModule],
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss'],
})
export default class LoginComponent {
  fb = inject(FormBuilder);
  loginForm!: FormGroup;

  authService = inject(AuthService);

  router = inject(Router);

  ngOnInit() {
    this.loginForm = this.fb.group({
      email: ['', Validators.compose([Validators.required, Validators.email])],
      password: ['', Validators.required],
    });
  }

  login() {
    // console.log(this.loginForm.value);
    this.authService.loginService(this.loginForm.value).subscribe({
      next: (res) => {
        alert('Login is success!');
        this.router.navigate(['home']);
        this.loginForm.reset();
      },
      error: (err) => {
        console.log(err);
        alert(err.error.message)
      },
    });
  }
}
<!-- <p>login works!</p> -->
<div class="lg:w-3/12 mt-10 p-10 shadow-lg mx-auto">
  <h3 class="text-3xl text-center font-semibold py-5">Login</h3>
  <form [formGroup]="loginForm" class="flex flex-col">
    <div class="flex flex-col mt-2 mb-1">
      <input
        formControlName="email"
        class="p-2 border border-gray-300 rounded"
        type="text"
        placeholder="Email"
      />
      <span
        *ngIf="
          loginForm.hasError('required', 'email') &&
          loginForm.controls['email'].dirty
        "
        class="text-red-500"
        >*Email is required</span
      >
      <span *ngIf="loginForm.hasError('email', 'email')" class="text-red-500"
        >*Email is invalid</span
      >
    </div>

    <div class="flex flex-col mt-2 mb-1">
      <input
        formControlName="password"
        class="p-2 border border-gray-300 rounded"
        type="password"
        placeholder="Password"
      />
      <span
        *ngIf="
          loginForm.hasError('required', 'password') &&
          loginForm.controls['password'].dirty
        "
        class="text-red-500"
        >*Password is required</span
      >
    </div>

    <button
      [disabled]="loginForm.invalid"
      [class.disabled]="loginForm.invalid"
      (click)="login()"
      class="bg-indigo-500 p-2 rounded mt-3 text-white"
    >
      Login
    </button>
  </form>
  <div class="flex justify-between mt-2">
    <a routerLink="/forget-password" class="text-indigo-600 cursor-pointer">Forget Password</a>
    <a routerLink="/register" class="text-indigo-600 cursor-pointer">Create Account</a>
  </div>
</div>
